import React, { useState } from "react";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");

  const handleLogin = () => {
    if (username.trim()) onLogin(username);
  };

  return (
    <div className="flex h-screen items-center justify-center bg-gradient-to-r from-blue-700 to-purple-700 text-white">
      <div className="bg-gray-900 p-8 rounded-xl shadow-xl w-80">
        <h2 className="text-2xl font-bold mb-4">Login to AI Assistant</h2>
        <input
          type="text"
          placeholder="Enter your name"
          className="w-full p-2 mb-4 rounded bg-gray-700"
          onChange={(e) => setUsername(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="w-full bg-blue-500 hover:bg-blue-600 p-2 rounded font-semibold"
        >
          Login
        </button>
      </div>
    </div>
  );
};

export default Login;
