import React, { useState } from "react";
import { Paperclip, Mic, Send } from "lucide-react";

const MessageInput = ({ onSend }) => {
  const [message, setMessage] = useState("");

  const handleSend = () => {
    if (message.trim()) {
      onSend(message);
      setMessage("");
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") handleSend();
  };

  return (
    <div className="flex items-center gap-2 p-4 border-t border-gray-700 bg-[#0f172a]">
      <button className="text-gray-400 hover:text-white">
        <Paperclip size={20} />
      </button>

      <input
        type="text"
        className="flex-1 rounded-full bg-gray-800 text-white px-4 py-2 focus:outline-none"
        placeholder="Type a message..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyDown={handleKeyPress}
      />

      <button className="text-gray-400 hover:text-white">
        <Mic size={20} />
      </button>

      <button
        onClick={handleSend}
        className="text-gray-400 hover:text-white bg-blue-600 p-2 rounded-full"
      >
        <Send size={20} />
      </button>
    </div>
  );
};

export default MessageInput;
