import React, { useState } from "react";
import ChatArea from "../components/ChatArea";
import MessageInput from "../components/MessageInput";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header"; // ðŸ‘ˆ Import header

const Chat = () => {
  const [messages, setMessages] = useState([
    { sender: "ai", text: "Hi, how can I help you today?" },
    { sender: "user", text: "What is React?" },
  ]);

  return (
    <div className="h-screen flex">
      {/* Sidebar on the left */}
      <Sidebar />

      {/* Main content: Header + ChatArea + Input */}
      <div className="flex-1 flex flex-col">
        <Header /> {/* ðŸ‘ˆ Use Header here */}
        <ChatArea messages={messages} />
        <MessageInput onSend={(text) => setMessages([...messages, { sender: "user", text }])} />
      </div>
    </div>
  );
};

export default Chat;
