const Sidebar = ({ history }) => (
  <div className="w-60 bg-gray-900 p-4 text-white overflow-y-auto">
    <h3 className="text-lg font-bold mb-2">History</h3>
    {history.map((item, i) => (
      <div key={i} className="text-sm p-2 hover:bg-gray-700 rounded">
        {item.slice(0, 40)}...
      </div>
    ))}
  </div>
);

export default Sidebar;
