import React from "react";

const Header = () => {
  return (
    <header className="bg-[#1e293b] text-white px-6 py-4 shadow-md flex justify-between items-center">
      {/* Logo + App Name */}
      <div className="flex items-center space-x-3">
        <img
          src="/logo.png" // <-- replace with your own logo path in `public/`
          alt="App Logo"
          className="w-10 h-10 rounded-full"
        />
        <h1 className="text-xl font-bold">Voice Chat AI</h1>
      </div>

      {/* Future: Profile section or settings */}
      <div>
        <span className="text-sm text-gray-300">Welcome, User</span>
      </div>
    </header>
  );
};

export default Header;
