import React, { useState } from 'react';
import ChatWindow from './components/ChatWindow';
import ChatInput from './components/ChatInput';

function App() {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Hello! How can I assist you today?' },
  ]);

  const sendMessage = (messageText) => {
    const newMessages = [...messages, { sender: 'user', text: messageText }];
    setMessages(newMessages);
    setTimeout(() => {
      setMessages((prev) => [...prev, { sender: 'bot', text: 'You said: ' + messageText }]);
    }, 1000);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-800 to-gray-900 text-white flex items-center justify-center">
      <div className="w-full max-w-3xl h-[80vh] bg-gray-950 rounded-2xl shadow-xl p-4 flex flex-col">
        <ChatWindow messages={messages} />
        <ChatInput onSend={sendMessage} />
      </div>
    </div>
  );
}

export default App;
