import React from 'react';
import { FaUserCircle, FaSignOutAlt, FaComments } from 'react-icons/fa';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const Sidebar = ({ onLogout, historyItems }) => {
  const navigate = useNavigate();

  return (
    <motion.div 
      initial={{ x: -200 }} 
      animate={{ x: 0 }} 
      transition={{ type: 'spring', stiffness: 60 }}
      className="w-64 min-h-screen bg-[#1f2937] text-white flex flex-col shadow-lg"
    >
      <div className="flex items-center justify-center gap-2 py-6 border-b border-gray-700">
        <FaUserCircle className="text-3xl text-gray-300" />
        <span className="text-xl font-semibold">AI Assistant</span>
      </div>

      <div className="flex-1 overflow-auto px-4 py-2">
        <h2 className="text-sm font-medium mb-3 text-gray-400">Chat History</h2>
        <ul className="space-y-2">
          {historyItems?.map((item, index) => (
            <li 
              key={index} 
              className="bg-gray-700 hover:bg-gray-600 p-2 rounded-lg cursor-pointer flex items-center gap-2"
              onClick={() => navigate(`/chat/${item.id}`)}
            >
              <FaComments className="text-sm" />
              <span className="truncate">{item.title}</span>
            </li>
          )) || <p className="text-sm text-gray-500">No chats yet</p>}
        </ul>
      </div>

      <div className="p-4 border-t border-gray-700">
        <button 
          onClick={onLogout} 
          className="w-full flex items-center justify-center gap-2 py-2 bg-red-600 hover:bg-red-700 rounded-lg"
        >
          <FaSignOutAlt /> Logout
        </button>
      </div>
    </motion.div>
  );
};

export default Sidebar;
